import { Component, Input } from '@angular/core';
import { ApiService } from '../service/api.service';

@Component({
  selector: 'app-sell-modal',
  templateUrl: './sell-modal.component.html',
  styleUrls: ['./sell-modal.component.scss']
})
export class SellModalComponent {
  @Input() medicamentId: number;
  @Input() modalVisible: boolean;

  unitQuantity: number;
  totalPurchase: number;

  constructor(private apiService: ApiService) {
    this.medicamentId = 0;
    this.unitQuantity = 0;
    this.modalVisible = false;
    this.totalPurchase = 0;
  }

  calculateTotal() {
    const quantity = 10;
    const url = `http://localhost:8091/api/sale/get_unit_price/${this.medicamentId}?quantity=${quantity}`;
    this.apiService.getUnitPrice(url).subscribe(
      (price: number) => {
        this.totalPurchase = price * this.unitQuantity;
      },
      (error) => {
        console.log(error);
      }
    );
  }

  sellMedicament() {
    // Implementa la lógica de venta del medicamento
    // ...

    this.closeModal(); // Cierra el modal después de la venta
  }

  closeModal() {
    this.modalVisible = false; // Actualiza el estado de modalVisible a false
  }
}
