import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  constructor(private http: HttpClient) {}

  getMedicaments(): Observable<Medicament[]> {
    const url = 'http://localhost:8090/api/medicaments';
    return this.http.get<Medicament[]>(url);
  }

  updateMedicament(url: string, medicament: Medicament): Observable<any> {
    return this.http.put(url, medicament);
  }

  getUnitPrice(url: string): Observable<number> {
    return this.http.get<number>(url);
  }

  deleteMedicament(url: string): Observable<any> {
    return this.http.delete(url);
  }
}
