import { Component, OnInit } from '@angular/core';
import { MedicamentDto } from './dto/medicament.dto';
import { ApiService } from './service/api.service';
import { DatePipe } from '@angular/common';
import { SellModalComponent } from '../app/sell-modal/sell-modal.component';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  medicaments: MedicamentDto[] = [];
  formattedManufacturingDates: string[] = [];
  formattedExpirationDates: string[] = [];
  selectedMedicamentId: number;
  modalVisible: boolean = false;

  constructor(private apiService: ApiService, private datePipe: DatePipe) {
    this.selectedMedicamentId = 0;
  }

  ngOnInit() {
    this.getMedicaments();
  }

  getMedicaments() {
    this.apiService.getMedicaments().subscribe(
      response => {
        this.medicaments = response;
        this.formatManufacturingDates();
        this.formatExpirationDates();
      },
      error => {
        console.log(error);
      }
    );
  }

  formatManufacturingDates() {
    this.formattedManufacturingDates = this.medicaments.map(medicament => {
      return medicament.manufacturingDate
        ? this.datePipe.transform(medicament.manufacturingDate, 'dd/MM/yyyy')!
        : '';
    });
  }

  formatExpirationDates() {
    this.formattedExpirationDates = this.medicaments.map(medicament => {
      return medicament.expirationDate
        ? this.datePipe.transform(medicament.expirationDate, 'dd/MM/yyyy')!
        : '';
    });
  }

  updateMedicament(medicament: MedicamentDto) {
    const url = `http://localhost:8090/api/medicament/${medicament.id}`;
    this.apiService.updateMedicament(url, medicament).subscribe(
      response => {
        console.log('Medicamento actualizado exitosamente');
      },
      error => {
        console.log('Error al actualizar el medicamento', error);
      }
    );
  }

  deleteMedicament(medicament: MedicamentDto) {
    const url = `http://localhost:8090/api/medicament/${medicament.id}`;
    this.apiService.deleteMedicament(url).subscribe(
      response => {
        console.log('Medicamento eliminado exitosamente');
      },
      error => {
        console.log('Error al eliminar el medicamento', error);
      }
    );
  }

  getUnitPrice(medicament: MedicamentDto) {
    const quantity = 10;
    const url = `http://localhost:8091/api/sale/get_unit_price/${medicament.id}?quantity=${quantity}`;
    this.apiService.getUnitPrice(url).subscribe(
      unitPrice => {
        console.log('Precio unitario obtenido:', unitPrice);
        this.modalVisible = true;
      },
      error => {
        console.log('Error al obtener el precio unitario del medicamento', error);
      }
    );
  }

  openModal(medicamentId: number) {
    this.selectedMedicamentId = medicamentId;
    this.modalVisible = true;
  }

  getSelectedMedicament(): MedicamentDto {
    return this.medicaments.find(medicament => medicament.id === this.selectedMedicamentId) || new MedicamentDto();
  }

  onCloseModal() {
    this.modalVisible = false;
  }
}
<h2>Lista de Medicamentos</h2>
<p-table [value]="medicaments" [paginator]="true" styleClass="p-datatable-gridlines" [rows]="5" [showCurrentPageReport]="true" [tableStyle]="{ 'min-width': '50rem' }"
  currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
  [rowsPerPageOptions]="[10, 25, 50]">
  <ng-template pTemplate="caption">
    <div class="flex align-items-center justify-content-between">
        Medicaments

    </div>

</ng-template>
  <ng-template pTemplate="header">
    <tr>
      <th id="name" scope="col">Nombre</th>
      <th id="manufacturingDate" scope="col">Fecha de Fabricación</th>
      <th id="expirationDate" scope="col">Fecha de Vencimiento</th>
      <th id="quantity" scope="col">Cantidad en Stock</th>
      <th id="unitPrice" scope="col">Valor Unitario</th>
      <th id="actions" scope="col">Acciones</th> <!-- Nueva columna para las acciones -->
    </tr>
  </ng-template>
  <ng-template pTemplate="body" let-medicament>
    <tr>
      <td>{{ medicament.name }}</td>
      <td>{{ formattedManufacturingDates[medicaments.indexOf(medicament)] }}</td>
      <td>{{ formattedExpirationDates[medicaments.indexOf(medicament)] }}</td>
      <td>{{ medicament.quantity }}</td>
      <td>{{ medicament.unitPrice }}</td>
      <td>
        <button pButton type="button" icon="pi pi-pencil" (click)="updateMedicament(medicament)"></button>
        <button pButton type="button" icon="pi pi-shopping-cart" (click)="getUnitPrice(medicament)"></button>
        <button pButton type="button" icon="pi pi-trash" (click)="deleteMedicament(medicament)"></button>
      </td>
    </tr>    
  </ng-template>
</p-table>

<app-sell-modal [medicament]="getSelectedMedicament()" [modalVisible]="modalVisible" (closeModal)="onCloseModal()"></app-sell-modal>
