import { Component } from '@angular/core';
import { PersonDto } from './dto/person.dto';
import { ApiService } from './service/api.service';
import { environment } from 'src/environments/environment';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'front';
  date: Date;
  value: string;
  person: PersonDto;

  constructor(public apiService: ApiService, public router: Router) {
    this.date = null!;
    this.value = null!;
    this.person = new PersonDto();
    this.getCatalogue();
  }
  public save(): void {
    console.log(this.value);
    console.log(this.date);
    this.person.name = this.value;
    this.router.navigate(["persons"]);

    //

    //post
    /*this.apiService.post(environment.baseUrl + 'historiaclinica/api/personas', this.person, {}).subscribe({
      next: (data) => {
        console.log(data);
      },
      error: (e) => {
        var obj = e.error;
        console.log(e);
        Object.keys(obj).forEach(key => {
          //this._snackBar.open(key + ': ' + obj[key], 'Error');
        });
      },
      complete: () => this.router.navigate(["personas"]),
    });*/
  }

  public getCatalogue() {
    this.apiService.get(environment.baseUrl + 'historiaclinica/api/catalogue/' + btoa("5"), {}).subscribe((data: any) => {
      console.group(data)
    });
  }
}
